window.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector(".menu__burger"),t=document.querySelector(".header");e.addEventListener("click",(()=>{t.classList.toggle("open"),document.body.classList.toggle("lock")}));const n=document.querySelector(".arrow__btn-prev"),o=(document.querySelectorAll(".arrow__btn-next"),document.querySelector(".pets__content"));n.addEventListener("click",(()=>{o.classList.add("transition-prev")})),o.addEventListener("animationed",(()=>{o.classList.add("transition-prev")}))}));